mapper.insert:
INSERT INTO member (
userId, password, nickname, gender, enabled, created_at, provider, city, district
) VALUES (
#{userId}, #{password}, #{nickname}, #{gender}, #{enabled}, NOW(), #{provider}, #{city}, #{district}
)

INSERT INTO member_authority (
userId, authority
) VALUES (
#{userId}, 'ROLE_MEMBER'
)

mapper.update:
UPDATE member
password = #{password},
nickname = #{nickname},
city = #{city}, district = #{district}, info_until = #{infoUntil}
WHERE userId = #{userId}

