mapper.update:
UPDATE flag_post
SET title = #{title}, content = #{content}, status = 'PENDING'
WHERE id = #{id}

UPDATE flag_post
SET view_count = view_count + 1
WHERE id = #{id}

mapper.select:
SELECT COUNT(*) > 0
FROM flag_like
WHERE post_id = #{postId} AND member_id = #{memberId}

mapper.update:
UPDATE flag_post
SET like_count = like_count + 1
WHERE id = #{postId}

UPDATE flag_post
SET like_count = (SELECT COUNT(*) FROM flag_like WHERE post_id = #{postId})
WHERE id = #{postId}

UPDATE flag_post
SET status = #{status}
WHERE id = #{id}

mapper.select:
SELECT COUNT(*)
FROM flag_post
WHERE status = 'APPROVED' OR userId = #{value}
